package com.doctor.assistant.scheduleserver.config;import com.doctor.assistant.scheduleserver.interceptor.ResponseResultInterceptor;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;//@Configurationpublic class InterceptorConfig implements WebMvcConfigurer {	@Autowired	private ResponseResultInterceptor responseResultInterceptor;	@Override	public void addInterceptors(InterceptorRegistry registry) {		String apiUri = "/**";		//响应结果控制拦截		registry.addInterceptor(responseResultInterceptor).addPathPatterns(apiUri);	}}