package com.doctor.assistant.experiment.czy.feignclient;import feign.Feign;import feign.Target;import feign.codec.StringDecoder;import feign.form.spring.SpringFormEncoder;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.cloud.openfeign.FeignClientsConfiguration;import org.springframework.context.annotation.Import;import org.springframework.stereotype.Service;import java.net.URI;@Service@Import(FeignClientsConfiguration.class)public class FeignCalculateService {    private IFeignCalculateServer feignCalculateServer;    @Autowired    public FeignCalculateService(){        feignCalculateServer = Feign.builder()                .encoder(new SpringFormEncoder())                .decoder(new StringDecoder())                .target(Target.EmptyTarget.create(IFeignCalculateServer.class));    }    /**     * json 数据交给远程计算平台     * @param     * @return     */    public String calculate(URI baseUri, String calculateInputJson){        return feignCalculateServer.calculate(baseUri, calculateInputJson);    }}