package com.doctor.assistant.experiment.czy.feignclient;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;@FeignClient(value = "CALCULATE-SERVER", path = "/calculate")public interface IFeignCalculateClient {    /**     * json 数据交给远程计算平台     * @param     * @return     */    @RequestMapping("/inputJson/{calculateInputJson}")    String calculate(@PathVariable String calculateInputJson);}