package com.doctor.assistant.experiment.czy.feignclient;import feign.Param;import feign.RequestLine;import org.springframework.cloud.openfeign.FeignClient;import java.net.URI;@FeignClient(value = "USER-SERVER")public interface IFeignCalculateServer {    /**     * json 数据交给远程计算平台     * @param     * @return     */    @RequestLine("POST /calculate/inputJson/{calculateInputJson}")    String calculate(URI uri, @Param("calculateInputJson") String calculateInputJson);}